#!/usr/bin/env python3

import yaml
import rclpy
import threading
from .tasks.diana_training_point_cloud import DIANATrainingPointCloud
from ament_index_python.packages import get_package_share_directory


import os
#os.environ["CUDA_VISIBLE_DEVICES"]="-1" 
import tensorflow as tf

gpus = tf.config.experimental.list_physical_devices('GPU')

if gpus:
    try:
    # Currently, memory growth needs to be the same across GPUs
        for gpu in gpus:
            print("DEBUG: GPU found")
            tf.config.experimental.set_memory_growth(gpu, True)
            logical_gpus = tf.config.experimental.list_logical_devices('GPU')
            # print(len(gpus), "Physical GPUs,", len(logical_gpus), "Logical GPUs")
    except RuntimeError as e:
    # Memory growth must be set before GPUs have been initialized
        print(e)

def main(args=None):
    """
    """
    print("DEBUG: Starting main")
    configFilepath = os.path.join(
        get_package_share_directory("pic4rl"), 'config',
        'main_params.yaml'
    )

    with open(configFilepath, 'r') as file:
        configParams = yaml.safe_load(file)['main_node']['ros__parameters']

    rclpy.init()

    if configParams['task']=='goToPose':
        if configParams['sensor'] == 'lidar':
            pic4rl_training = DIANATrainingPointCloud()
            pic4rl_training.get_logger().info(
                "Initialized Training: sensor=LiDAR, task=goToPose\n\n")
        else:
            print("ERROR: Sensor not recognized")
            return
    else:
        print("ERROR: Task not recognized")
        return


    print("Starting thread")
    pic4rl_training.threadFunc()
    print("Thread started")

    pic4rl_training.destroy_node()
    rclpy.shutdown()

    # th = threading.Thread(target=pic4rl_training.threadFunc)    
    # th.start()
    
    # try:
    #     rclpy.spin(pic4rl_training)
    # except:
    #     pic4rl_training.destroy_node()
    #     th.join()
    #     rclpy.shutdown()

if __name__ == '__main__':
    main()